/* IMPORT */
import Buffer from 'node-buffer-encoding';
import is from './is.js';
/* MAIN */
const Node = {
    /* API */
    encode: (data) => {
        return Buffer.encode(data, 'hex');
    },
    encodeStr: (data) => {
        return Buffer.encodeStr(data, 'hex');
    },
    decode: (data) => {
        return Buffer.decode(data, 'hex');
    },
    decodeStr: (data) => {
        return Buffer.decodeStr(data, 'hex');
    },
    is
};
/* EXPORT */
export default Node;
